import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Subscription } from 'rxjs';
import { Account, post } from 'src/app/models/account';
import { ApiService } from 'src/app/services/api.service';
import { first } from 'rxjs/operators';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss'],
})
export class HomeComponent implements OnInit {
  images: any[] = [
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
    'https://scontent.fcai20-2.fna.fbcdn.net/v/t39.30808-6/277569556_2868843193415122_997654529348430605_n.jpg?_nc_cat=105&ccb=1-5&_nc_sid=5b7eaf&_nc_ohc=u6gSQ7tZxdgAX9X9XAQ&_nc_ht=scontent.fcai20-2.fna&oh=00_AT8q2s2Ki7k3H6VZWejE8hmMd6bF_ySBfQOFduFiBf8tJg&oe=624C44F3',
    'https://scontent.fcai20-2.fna.fbcdn.net/v/t39.30808-6/277558626_1551681598545402_222590933355412033_n.jpg?_nc_cat=103&ccb=1-5&_nc_sid=5b7eaf&_nc_ohc=n6Y0bPKsqZEAX8ucolx&_nc_oc=AQnlnUABjOcSTOTtSBe8FObzhhmCVRHmXkK7KZ-6e5lyAy9RL8r8ABTgom-u4VAsYl0&_nc_ht=scontent.fcai20-2.fna&oh=00_AT_ZUxQmriNksDrAIl1Z9XW8LhisAM5YGU_6GiRaR65x4g&oe=624CB7EE',
    'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRG8ZbrL9A8iXgiZ2hqrmov1mxZLVoEZ2YRrg&usqp=CAU',
  ];
  posts: post[] = [];
  user: Account | null = null;

  constructor(
    private apiService: ApiService,
    private route: ActivatedRoute,
    private _router: Router
  ) {}

  async ngOnInit(): Promise<void> {
    this.route.params.pipe(first()).subscribe(({ id }) => {
      this.apiService.getAccount(id).subscribe(
        (res) => {
          console.log(res);
          this.user = res;
        },
        (err) => {
          console.log(err);
        }
      );
    });
    this.apiService.getPosts().subscribe(
      (res) => {
        console.log(res);
        this.posts = res;
      },
      (err) => {
        console.log(err);
      }
    );
  }

  postMessage(form: NgForm): void {
    const { message } = form.value;
    let data: any = {
      message: message,
      fname: this.user?.fname,
      lname: this.user?.lname,
      avatar: this.user?.avatar,
      likes: Math.floor(Math.random() * 10),
    };

    this.apiService.postPost(data).subscribe(
      (res) => {
        console.log(res);
      },
      (err) => {
        console.log(err);
      }
    );

    form.resetForm();
  }

  logout(): void {
    this.apiService.isLoggedIn = false;
    this._router.navigate([``]);
  }
}
